package javaprograms;

public class Arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String [] laptops= {"dell","lenovo","sony"};
		
		String [] mobile=new String[3];
		String s1="Ford";
		String s2="Band";
		mobile[0]="samsung";
		mobile[1]="moto";
		mobile[2]="iphone";
		
				
		System.out.println(laptops[0]);  // index position is 0
		System.out.println(laptops[1]); // 
		System.out.println(laptops[2]);
		System.out.println();
		System.out.println("The length of array is: "+laptops.length);
		
		System.out.println(s1.compareTo(s2));

	}

}
