package javaprograms;

public class Nested {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=100;
		int b=50;
		int c=20;
		
		if(a>b)    // external
		{
			
				if(a>c)    // internal
				{
					System.out.println("a is bigger");
				}
									
		else
		{
			System.out.println("b is bigger than a");
		}
		}
	}
}
		
	
			

	


