package javaprograms;

public class Strings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1="Technology";
		String s2="Automation";
		
		
		System.out.println("Welcome to Automation");
		
		
		System.out.println("The length is :"+s1.length());
		
		System.out.println("Are both Strings Equal?: "+s1==s2);
		System.out.println("Are both Strings Equal?: "+s1.equals(s2));
		
				
	}

}
